mapper.update:
UPDATE CD_O_DEVICE
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, MATERIAL_ID = #{ materialId,jdbcType=INTEGER}, BRAND = #{ brand,jdbcType=VARCHAR}, MODEL = #{ model,jdbcType=VARCHAR}, VOLTAGE = #{ voltage,jdbcType=DECIMAL}, POWER = #{ power,jdbcType=DECIMAL}, ANLB = #{ anlb,jdbcType=DECIMAL}, DIRECTION = #{ direction,jdbcType=LONGVARCHAR}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE CD_O_DEVICE
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, MATERIAL_ID = #{item.materialId,jdbcType=INTEGER}, BRAND = #{item.brand,jdbcType=VARCHAR}, MODEL = #{item.model,jdbcType=VARCHAR}, VOLTAGE = #{item.voltage,jdbcType=DECIMAL}, POWER = #{item.power,jdbcType=DECIMAL}, ANLB = #{item.anlb,jdbcType=DECIMAL}, DIRECTION = #{item.direction,jdbcType=LONGVARCHAR}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  CD_O_DEVICE
SET
DELETED_FLAG  =  '1'  WHERE ROW_ID = #{item}

mapper.select:
SELECT *
FROM CD_O_DEVICE WHERE DELETED_FLAG='0' AND STATUS = '1' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT *
FROM CD_O_DEVICE WHERE DELETED_FLAG='0' AND STATUS = '1'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}

SELECT *
FROM CD_O_DEVICE
WHERE DELETED_FLAG='0' AND STATUS = '1'  ${condition}
