mapper.update:
UPDATE CD_O_RAW_MATERIAL
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, MATERIAL_ID = #{ materialId,jdbcType=INTEGER}, RAW_MATERIAL_TYPE = #{ rawMaterialType,jdbcType=VARCHAR}, SHELF_LIFE = #{ shelfLife,jdbcType=INTEGER}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE CD_O_RAW_MATERIAL
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, MATERIAL_ID = #{item.materialId,jdbcType=INTEGER}, RAW_MATERIAL_TYPE = #{item.rawMaterialType,jdbcType=VARCHAR}, SHELF_LIFE = #{item.shelfLife,jdbcType=INTEGER}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  CD_O_RAW_MATERIAL
SET
DELETED_FLAG  =  '1'  WHERE ROW_ID = #{item}

mapper.select:
SELECT *
FROM CD_O_RAW_MATERIAL WHERE DELETED_FLAG='0' AND STATUS = '1' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT *
FROM CD_O_RAW_MATERIAL WHERE DELETED_FLAG='0' AND STATUS = '1'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}

SELECT *
FROM CD_O_RAW_MATERIAL
WHERE DELETED_FLAG='0' AND STATUS = '1'  ${condition}
