mapper.update:
UPDATE PP_L_BILL_NURSE
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, STATUS = #{ status,jdbcType=CHAR}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, LINE_NUMBER = #{ lineNumber,jdbcType=INTEGER}, PARITY = #{ parity,jdbcType=INTEGER}, SWINERY_ID = #{ swineryId,jdbcType=INTEGER}, PIG_ID = #{ pigId,jdbcType=INTEGER}, WEAN_NUM = #{ weanNum,jdbcType=TINYINT}, WEAN_WEIGHT = #{ weanWeight,jdbcType=DECIMAL}, LEAVE_QTY = #{ leaveQty,jdbcType=INTEGER}, FOSTER_NUM = #{ fosterNum,jdbcType=TINYINT}, BOARD_SOW_ID = #{ boardSowId,jdbcType=INTEGER}, HOUSE_ID = #{ houseId,jdbcType=INTEGER}, PIGPEN_ID = #{ pigpenId,jdbcType=INTEGER}, BILL_ID = #{ billId,jdbcType=INTEGER}, BABY_NUM = #{ babyNum,jdbcType=TINYINT}, WORKER = #{ worker,jdbcType=INTEGER}, CREATE_ID = #{ createId,jdbcType=INTEGER}, CREATE_DATE = #{ createDate,jdbcType=TIMESTAMP}, IN_HOUSE_ID = #{ inHouseId,jdbcType=INTEGER}, IN_PIGPEN_ID = #{ inPigpenId,jdbcType=INTEGER}, CHANGE_HOUSE_DATE = #{ changeHouseDate,jdbcType=TIMESTAMP}, IN_PIG_ID = #{ inPigId,jdbcType=INTEGER}, IN_PIG_HOUSE_ID = #{ inPigHouseId,jdbcType=INTEGER}, IN_PIG_PIGPEN_ID = #{ inPigPigpenId,jdbcType=INTEGER}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE PP_L_BILL_NURSE
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, STATUS = #{item.status,jdbcType=CHAR}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, LINE_NUMBER = #{item.lineNumber,jdbcType=INTEGER}, PARITY = #{item.parity,jdbcType=INTEGER}, SWINERY_ID = #{item.swineryId,jdbcType=INTEGER}, PIG_ID = #{item.pigId,jdbcType=INTEGER}, WEAN_NUM = #{item.weanNum,jdbcType=TINYINT}, WEAN_WEIGHT = #{item.weanWeight,jdbcType=DECIMAL}, LEAVE_QTY = #{item.leaveQty,jdbcType=INTEGER}, FOSTER_NUM = #{item.fosterNum,jdbcType=TINYINT}, BOARD_SOW_ID = #{item.boardSowId,jdbcType=INTEGER}, HOUSE_ID = #{item.houseId,jdbcType=INTEGER}, PIGPEN_ID = #{item.pigpenId,jdbcType=INTEGER}, BILL_ID = #{item.billId,jdbcType=INTEGER}, BABY_NUM = #{item.babyNum,jdbcType=TINYINT}, WORKER = #{item.worker,jdbcType=INTEGER}, CREATE_ID = #{item.createId,jdbcType=INTEGER}, CREATE_DATE = #{item.createDate,jdbcType=TIMESTAMP}, IN_HOUSE_ID = #{item.inHouseId,jdbcType=INTEGER}, IN_PIGPEN_ID = #{item.inPigpenId,jdbcType=INTEGER}, CHANGE_HOUSE_DATE = #{item.changeHouseDate,jdbcType=TIMESTAMP}, IN_PIG_ID = #{item.inPigId,jdbcType=INTEGER}, IN_PIG_HOUSE_ID = #{item.inPigHouseId,jdbcType=INTEGER}, IN_PIG_PIGPEN_ID = #{item.inPigPigpenId,jdbcType=INTEGER}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  PP_L_BILL_NURSE
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}
AND ${item.RECORD_CON} = #{item.RECORD_VALUES}

UPDATE  PP_L_BILL_NURSE
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' ${item}

mapper.select:
SELECT 

FROM PP_L_BILL_NURSE WHERE DELETED_FLAG='0' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT 

FROM PP_L_BILL_NURSE WHERE DELETED_FLAG='0'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}
