mapper.update:
UPDATE SC_M_DAILY_RECORD
STATUS = #{ status,jdbcType=CHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, MATERIAL_ID = #{ materialId,jdbcType=INTEGER}, QUANTITY = #{ quantity,jdbcType=DECIMAL}, PLAN_DATE = #{ planDate,jdbcType=DATE}, REQUIRE_ID = #{ requireId,jdbcType=INTEGER}, CREATE_ID = #{ createId,jdbcType=INTEGER}, CREATE_DATE = #{ createDate,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE SC_M_DAILY_RECORD
STATUS = #{item.status,jdbcType=CHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, MATERIAL_ID = #{item.materialId,jdbcType=INTEGER}, QUANTITY = #{item.quantity,jdbcType=DECIMAL}, PLAN_DATE = #{item.planDate,jdbcType=DATE}, REQUIRE_ID = #{item.requireId,jdbcType=INTEGER}, CREATE_ID = #{item.createId,jdbcType=INTEGER}, CREATE_DATE = #{item.createDate,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  SC_M_DAILY_RECORD
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}
AND ${item.RECORD_CON} = #{item.RECORD_VALUES}

UPDATE  SC_M_DAILY_RECORD
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' ${item}

mapper.select:
SELECT 

FROM SC_M_DAILY_RECORD WHERE DELETED_FLAG='0' 
AND FARM_ID=#{ farmId,jdbcType=INTEGER}

SELECT 

FROM SC_M_DAILY_RECORD WHERE DELETED_FLAG='0'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}
